// Lead Scoring Algorithm - Using Correct Data Paths
let score = 0;

console.log('=== LEAD SCORING WITH CORRECT DATA PATHS ===');

// Extract data using the exact paths provided
const email = $('Email Valid?').first().json.data.email;
const emailDomain = $input.first().json.data.domain;
const industry = $input.first().json.data.industry;
const headcount = $input.first().json.data.headcount;
const country = $input.first().json.data.country;
const organization = $input.first().json.data.organization;
const emailValidationStatus = $('Email Valid?').first().json.data.status; // validation status
const jobTitle = $('Webhook Trigger').first().json.body[0].job_title;

console.log('Extracted Data:', {
  email,
  emailDomain,
  industry,
  headcount,
  country,
  organization,
  emailValidationStatus,
  jobTitle
});

// Company classification
const majorTechCompanies = ['microsoft', 'google', 'apple', 'amazon', 'meta', 'salesforce', 'adobe', 'oracle', 'ibm', 'intel', 'nvidia'];
const companyName = (organization || '').toLowerCase();
const isMajorTechCompany = majorTechCompanies.some(company => companyName.includes(company));
const isFortuneCompany = isMajorTechCompany;

console.log('Company Analysis:', {
  companyName,
  isMajorTechCompany,
  isFortuneCompany
});

console.log('\n=== SCORING BREAKDOWN ===');

// 1. EMAIL VALIDATION (30 points)
if (emailValidationStatus === 'valid') {
  score += 30;
  console.log('‚úÖ +30 points: Email validation confirmed as valid');
} else if (isFortuneCompany && emailDomain) {
  score += 25;
  console.log('‚úÖ +25 points: Fortune company email (validation override)');
} else if (emailDomain && !['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com'].includes(emailDomain)) {
  score += 20;
  console.log('‚úÖ +20 points: Corporate email domain');
}

// 2. COMPANY SIZE & PRESTIGE (35 points)
if (isFortuneCompany) {
  score += 35;
  console.log(`‚úÖ +35 points: Fortune-level company (${organization})`);
} else if (headcount && (headcount.includes('10000+') || headcount.includes('5001-10000'))) {
  score += 30;
  console.log('‚úÖ +30 points: Very large company (5000+ employees)');
} else if (headcount && headcount.includes('1001-5000')) {
  score += 25;
  console.log('‚úÖ +25 points: Large company (1001-5000)');
} else if (headcount && headcount.includes('501-1000')) {
  score += 20;
  console.log('‚úÖ +20 points: Medium company (501-1000)');
} else if (headcount && headcount.includes('201-500')) {
  score += 15;
  console.log('‚úÖ +15 points: Small-medium company (201-500)');
} else if (headcount && headcount.includes('51-200')) {
  score += 10;
  console.log('‚úÖ +10 points: Small company (51-200)');
} else if (headcount) {
  score += 5;
  console.log('‚úÖ +5 points: Company size data available');
}

// 3. JOB TITLE & SENIORITY (35 points)
const jobTitleLower = (jobTitle || '').toLowerCase();
console.log('Analyzing job title:', jobTitleLower);

if (jobTitleLower.includes('ceo') || jobTitleLower.includes('chief executive') || 
    jobTitleLower.includes('founder') || jobTitleLower.includes('president')) {
  score += 35;
  console.log('‚úÖ +35 points: C-level/President/Founder title');
} else if (jobTitleLower.includes('cto') || jobTitleLower.includes('cfo') || 
           jobTitleLower.includes('coo') || jobTitleLower.includes('chief')) {
  score += 30;
  console.log('‚úÖ +30 points: C-suite title');
} else if (jobTitleLower.includes('vp') || jobTitleLower.includes('vice president') || 
           jobTitleLower.includes('svp')) {
  score += 25;
  console.log('‚úÖ +25 points: VP title');
} else if (jobTitleLower.includes('director') || jobTitleLower.includes('head of')) {
  score += 20;
  console.log('‚úÖ +20 points: Director title');
} else if (jobTitleLower.includes('manager') || jobTitleLower.includes('lead')) {
  score += 15;
  console.log('‚úÖ +15 points: Management title');
} else if (jobTitleLower.includes('senior') || jobTitleLower.includes('principal')) {
  score += 10;
  console.log('‚úÖ +10 points: Senior title');
} else if (jobTitle) {
  score += 5;
  console.log('‚úÖ +5 points: Job title provided');
}

// 4. INDUSTRY RELEVANCE (25 points)
const industryLower = (industry || '').toLowerCase();
if (isMajorTechCompany || industryLower.includes('technology') || 
    industryLower.includes('software') || industryLower.includes('development') || 
    industryLower.includes('saas')) {
  score += 25;
  console.log('‚úÖ +25 points: Technology/Software industry');
} else if (industryLower.includes('finance') || industryLower.includes('fintech') || 
           industryLower.includes('banking')) {
  score += 20;
  console.log('‚úÖ +20 points: Finance industry');
} else if (industryLower.includes('healthcare') || industryLower.includes('medical')) {
  score += 15;
  console.log('‚úÖ +15 points: Healthcare industry');
} else if (industryLower.includes('consulting') || industryLower.includes('professional services')) {
  score += 12;
  console.log('‚úÖ +12 points: Consulting industry');
} else if (industry) {
  score += 5;
  console.log('‚úÖ +5 points: Industry data available');
}

// 5. GEOGRAPHIC QUALITY (5 points)
if (country === 'US') {
  score += 5;
  console.log('‚úÖ +5 points: US-based company');
}

// 6. JACKPOT BONUS (20 points)
if (isFortuneCompany && (jobTitleLower.includes('ceo') || jobTitleLower.includes('founder') || 
    jobTitleLower.includes('president'))) {
  score += 20;
  console.log('üöÄ +20 points: JACKPOT BONUS - C-level at Fortune company!');
} else if (isFortuneCompany && (jobTitleLower.includes('vp') || jobTitleLower.includes('director'))) {
  score += 15;
  console.log('üöÄ +15 points: Executive at Fortune company');
}

// Calculate deal value
let dealValueMultiplier = 1000;
if (isFortuneCompany) {
  dealValueMultiplier = 4000;
} else if (headcount && (headcount.includes('5001') || headcount.includes('10000+'))) {
  dealValueMultiplier = 2500;
} else if (headcount && headcount.includes('1001-5000')) {
  dealValueMultiplier = 1800;
}

const estimatedDealValue = Math.round(score * dealValueMultiplier);

// Determine lead temperature
let temperature = 'Cold';
let priority = 'Low';
let actionRequired = 'Add to newsletter';

if (score >= 120) {
  temperature = 'Hot';
  priority = 'Urgent';
  actionRequired = 'Call immediately - high value prospect';
} else if (score >= 80) {
  temperature = 'Warm';
  priority = 'High';
  actionRequired = 'Add to nurture sequence - follow up within 24 hours';
} else if (score >= 50) {
  temperature = 'Cool';
  priority = 'Medium';
  actionRequired = 'Add to long-term nurture sequence';
} else {
  temperature = 'Cold';
  priority = 'Low';
  actionRequired = 'Add to newsletter - quarterly follow-up';
}

console.log('\n=== FINAL RESULTS ===');
console.log('Total Score:', score);
console.log('Temperature:', temperature);
console.log('Priority:', priority);
console.log('Deal Value:', estimatedDealValue);
console.log('Deal Multiplier:', dealValueMultiplier);

// Return enriched data
return [{
  json: {
    // Original contact data
    email: email,
    job_title: jobTitle,
    company: organization,
    industry: industry,

    // Company data
    company_data: {
      domain: emailDomain,
      organization: organization,
      industry: industry,
      headcount: headcount,
      country: country
    },

    // Scoring results
    lead_score: score,
    temperature: temperature,
    priority: priority,
    action_required: actionRequired,
    estimated_deal_value: estimatedDealValue,
    scoring_timestamp: new Date().toISOString(),
    email_domain: emailDomain,
    headcount_category: headcount,
    industry_category: industry,
    company_tier: isFortuneCompany ? 'Fortune' : 'Standard',
    is_major_tech_company: isMajorTechCompany,
    deal_value_multiplier: dealValueMultiplier
  }
}];

